CREATE TABLE CUSTOMER (	CUSTOMER_ID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	DOGOVOR_NO TEXT(20),	SURNAME TEXT(100),	FIRSTNAME TEXT(30),	MIDLENAME TEXT(30));CREATE TABLE EQUIPMENT (	EID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	NAME TEXT(50) NOT NULL,	IP TEXT(15),	MAC TEXT(18));CREATE TABLE TV_LAN (	LAN_ID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	CUSTOMER_ID INTEGER NOT NULL,	IP TEXT(15) NOT NULL,	PORT TEXT(10),	NOTICE TEXT(500),	EQ_ID INTEGER,	CONSTRAINT TV_LAN_CUSTOMER_FK FOREIGN KEY (CUSTOMER_ID) REFERENCES CUSTOMER(CUSTOMER_ID),	CONSTRAINT TV_LAN_EQUIPMENT_FK FOREIGN KEY (EQ_ID) REFERENCES EQUIPMENT(EID));CREATE TABLE SERVICES (	SERVICE_ID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	NAME TEXT(60) NOT NULL,	BUSINESS_TYPE INTEGER);CREATE TABLE SUBSCR_SERV (	SUBSCR_SERV_ID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	CUSTOMER_ID INTEGER NOT NULL,	SERV_ID INTEGER NOT NULL,	STATE_SGN INTEGER NOT NULL,	STATE_DATE DATE(),	CONSTRAINT SUBSCR_SERV_SERVICES_FK FOREIGN KEY (SERV_ID) REFERENCES SERVICES(SERVICE_ID),	CONSTRAINT SUBSCR_SERV_CUSTOMER_FK FOREIGN KEY (CUSTOMER_ID) REFERENCES CUSTOMER(CUSTOMER_ID));
